function initPage(){initFooter(),initScrol()}function initFooter(){jQuery(document).on("scroll",function(){jQuery(window).scrollTop()>200?jQuery("#footer").addClass("visible"):jQuery("#footer").removeClass("visible")})}function initScrol(){$(".links-blocks").on("click","a",function(o){o.preventDefault();var i=$(this).attr("href"),t=$(i).offset().top-10;$("body,html").animate({scrollTop:t},1500)})}$(document).ready(initPage);